import { NavLink } from 'react-router-dom'
import React, { useState } from 'react';
import MenuIcon from '@material-ui/icons/Menu';
import CloseIcon from '@material-ui/icons/Close';
import { useEffect } from 'react';
import { useRef } from 'react';

const Navbar = () => {
    
    const dropDown = useRef(null);
    const[showmenu, setshowmenu] = useState(false);

    useEffect(() =>{
         document.title='Healhify';

         const outsideClick = (e) =>{
                 if(dropDown.current !== null && !dropDown.current.contains(e.target)){
                     setshowmenu(!showmenu)
                 }

                 if(showmenu){
                     window.addEventListener('click', outsideClick)
                 }
                 return () =>{
                     window.removeEventListener('click', outsideClick)
                 }
         }
    }, [showmenu])

    return (
        <>
          <div className='main_nav' id={showmenu ? 'hiddenmain' : 'hidden'} ref={dropDown}>
              <NavLink to='/'  exact activeClassName='active_links' className='main_links'>Home</NavLink>
            
              <NavLink to='/join' exact activeClassName='active_links' className='main_links'>Join</NavLink>
          </div>  
          <div className='burger-menu' onClick={() =>{
             setshowmenu(!showmenu) 
           }} >
             {showmenu ? <CloseIcon /> : <MenuIcon />}
           </div>
        </>
    )
}

export default Navbar
